var fs = require('fs')
  , qs = require('./qs')
  , path = require('path')
  , join = path.join
  , basename = path.basename
  , normalize = path.normalize
  , utils = require('./utils')
  , Buffer = require('buffer').Buffer
  , parse = require('url').parse
  , mime = require('mime');

//todo: seperate errback from httpError from notfound (throws if not isAsset)

exports.mime = mime;

exports.serve = function(req, res, next, opts) {
  "use strict";

  opts = opts || {};
  if (!opts.path) throw new Error('path required');
  opts.isAsset = true;

  // setup
  var head = opts.head = ('HEAD' == req.method)
    , get = 'GET' == req.method
    , root = opts.root ? normalize(opts.root) : null
    , getOnly = opts.getOnly
    , hidden = opts.hidden;

  // replace next() with callback when available
  if (opts.callback) next = opts.callback;

  // ignore non-GET requests
  if (getOnly && !get && !head) return next();

  // parse url
  var url = parse(opts.path)
    , path = qs.unescape(url.pathname);

  // null byte(s)
  if (~path.indexOf('\0')) return next(utils.error(400));

  // when root is not given, consider .. malicious
  if (!root && ~path.indexOf('..')) return next(utils.error(403));

  // index.html support
  if ('/' == path[path.length - 1]) path += 'index.html';

  // join / normalize from optional root dir
  path = normalize(join(root, path));

  // malicious path
  if (root && 0 != path.indexOf(root)) return next(utils.error(403));

  // "hidden" file
  if (!hidden && '.' == basename(path)[0]) return next();

};


/**
 * Transfer a specified file
 *
 * @param {ServerRequest}
 * @param {ServerResponse}
 * @param {Function} next
 * @param {Object} opts
 * @api private
 */

exports.send = function(req, res, next, opts) {
  "use strict";

  fs.stat(path, function(err, stat) {
    // mime type
    var fn = opts.callback
      , type = opts.ctype || mime.lookup(path)
      , maxAge = opts.maxAge || 0
      , done;

    // ignore ENOENT
    if (err) {
      if (fn) return fn(err);
      return ('ENOENT' == err.code || 'ENAMETOOLONG' == err.code) ? next() : next(err);
    } else if (stat.isDirectory()) {
      return next();
    }

    // header fields
    if (!res.getHeader('Date')) {
      res.setHeader('Date', new Date().toUTCString());
    }
    //if an asset/public resource was requested
    if (opts.isAsset) {
      if (!res.getHeader('Cache-Control')) {
        res.setHeader('Cache-Control', 'public, max-age=' + (maxAge / 1000));
      }
      if (!res.getHeader('Last-Modified')) {
        res.setHeader('Last-Modified', stat.mtime.toUTCString());
      }
      res.setHeader('Accept-Ranges', 'bytes');
    }
    if (!res.getHeader('Content-Type')) {
      var charset = mime.charsets.lookup(type);
      res.setHeader('Content-Type', type + (charset ? '; charset=' + charset : ''));
    }

    // conditional GET support
    if (opts.isAsset && utils.conditionalGET(req)) {
      if (!utils.modified(req, res)) {
        return utils.notModified(res);
      }
    }

    var streamOpts = {}, len = stat.size;

    // we have a Range request
    var ranges = req.headers.range;
    if (opts.isAsset && ranges) {
      ranges = utils.parseRange(len, ranges);

      // valid
      if (ranges) {
        streamOpts.start = ranges[0].start;
        streamOpts.end = ranges[0].end;

        // unsatisfiable range
        if (streamOpts.start > len - 1) {
          res.setHeader('Content-Range', 'bytes */' + stat.size);
          return next(utils.error(416));
        }

        // limit last-byte-pos to current length
        if (streamOpts.end > len - 1) streamOpts.end= len - 1;

        // Content-Range
        len = streamOpts.end - streamOpts.start + 1;
        res.statusCode = 206;
        res.setHeader('Content-Range', 'bytes ' + streamOpts.start + '-' + streamOpts.end + '/' + stat.size);
      }
    }

    res.setHeader('Content-Length', len);

    // transfer
    if (opts.head) return res.end();

    // stream
    var stream = fs.createReadStream(path, streamOpts);
    req.on('close', stream.destroy.bind(stream));
    stream.pipe(res);

    // callback
    if (fn) {
      var callback = function(err) { done || fn(err); done = true };
      req.on('close', callback);
      req.socket.on('error', callback);
      stream.on('error', callback);
      stream.on('end', callback);
    } else {
      stream.on('error', function(err) {
        if (res.headerSent) {
          console.error(err.stack);
          req.destroy();
        } else {
          next(err);
        }
      });
    }
  });
};